mapper.select:
select a.*,b.name from ${dbname} as a left join
xhgmnet.xhgmnet_bs_station as b on
a.Call_TS_Id=b.bsId where 1=1

	and a.Caller like concat(#{Caller},'%')


	and a.Called like concat(#{Called},'%')


	and a.Call_Time >=#{starttime,jdbcType=DATE}


	and a.Call_Time <=#{endtime,jdbcType=DATE}

order by a.Call_Time desc limit #{start},#{limit}

select count(*) from ${dbname} where 1=1

	and Caller like concat(#{Caller},'%')


	and Called like concat(#{Called},'%')


	and Call_Time >=#{starttime,jdbcType=DATE}


	and Call_Time <=#{endtime,jdbcType=DATE}


	select sum(Use_Time) as usetime,count(id) as num, DATE_FORMAT(Call_Time, 'd-%d' ) AS
	date from ${dbname} where 1=1


	select sum(Use_Time) as usetime,count(id) as num ,DATE_FORMAT(Call_Time, 'h-%H' ) AS date
	from ${dbname} where  date_format(Call_Time,'%Y-%m-%d')=#{time}


	and Call_TS_Id=#{bsId}

 group by date order by date asc
