mapper.select:
		select otherTab.*,conf.siteId,station.name,station.level,station.zone from 
		(select a.*,b.deviceName from xhgmnet_emh_sensor_alarm as a left join xhgmnet_emh_device as b on a.deviceId=b.deviceId
		) as otherTab LEFT JOIN xhgmnet_emh_config as conf on otherTab.fsuId=conf.fsuId LEFT JOIN 
		xhgmnet_bs_station as station on conf.siteId=station.bsId where 1=1 

			and 1=0


			and otherTab.deviceId in

      			#{item}



			and alarmlevel=#{alarmlevel}


			and alarmFlag=#{alarmFlag}


			and level=#{bsLevel}


			and zone=#{bsArea}

		group by serialNo order by alarmTime desc limit #{start},#{limit}

		select count(*) from (select otherTab.*,conf.siteId,station.name,station.level,station.zone from 
		(select a.*,b.deviceName from xhgmnet_emh_sensor_alarm as a left join xhgmnet_emh_device as b on a.deviceId=b.deviceId
		) as otherTab LEFT JOIN xhgmnet_emh_config as conf on otherTab.fsuId=conf.fsuId LEFT JOIN 
		xhgmnet_bs_station as station on conf.siteId=station.bsId where 1=1 

			and 1=0


			and otherTab.deviceId in

      			#{item}



			and alarmlevel=#{alarmlevel}


			and alarmFlag=#{alarmFlag}


			and level=#{bsLevel}


			and zone=#{bsArea}

		group by serialNo) as numAlarm
