mapper.select:
select a.userId,a.bsId,b.E_name as name from xhgmnet_operations_userstatus as a left join xhgmnet_radiouser as b on a.userId=b.C_ID where a.bsId=#{bsId} and regStatus in(1,2,3)
and a.userId in (select C_ID from xhgmnet.xhgmnet_radiouser where E_vpnId=#{vpnId})
limit #{start},#{limit}

select a.gourpId,a.bsId,b.E_name as talkgroupName,c.E_name as multigroupName from xhgmnet_bs_groupstatus as a left join xhgmnet_talkgroup as b on a.gourpId=b.TalkgroupID left join xhgmnet_multigroup as c on a.gourpId=c.C_MultigroupID where a.bsId=#{bsId}
and (a.gourpId in (select TalkgroupID from xhgmnet.xhgmnet_talkgroup where E_vpnId=#{vpnId}) or a.gourpId in (select C_MultigroupID from xhgmnet.xhgmnet_multigroup where E_vpnId=#{vpnId}))
limit #{start},#{limit}

select count(gourpId) from xhgmnet_bs_groupstatus where  bsId=#{bsId}

 and (gourpId in (select TalkgroupID  from xhgmnet.xhgmnet_talkgroup where E_vpnId=#{vpnId}) 
 or gourpId in (select C_MultigroupID from xhgmnet.xhgmnet_multigroup where E_vpnId=#{vpnId}))
