	and serialNumber like concat(#{serialNumber},'%')


	and `from`=#{from}


	and status=#{status}


	and isLock=1 and addUser=#{user}


	and addUser=#{user}


	and isLock=0


	and applyTag=#{applyTag}

mapper.delete:
delete from xhgmnet_business_assetinfo where id in(

	#{id}

)

