mapper.select:
     	select a.*,b.reason as reason2,b.suggestion as suggestion2 from xhgmnet_eastcom_alarminfo  as a
     	left join xhgmnet_eastcom_knowledge as b on a.neType=b.neType and 
     	a.modType=b.modType and a.subModType=b.subModType and a.category=b.category 
     	and a.code=b.code  and a.causeCode=b.causeCode

     	where  1=1

     	and a.level in(

			#{id}

		)


     	and a.neType in(

			#{id}

		)


     	and a.time between #{startTime} and #{endTime}


     	and a.category in(

			#{id}

		)


     	and a.status in(

			#{id}

		)


		order by a.time desc limit #{start},#{limit}

     	select count(*) from xhgmnet_eastcom_alarminfo  where  1=1


     	and level in(

			#{id}

		)


     	and neType in(

			#{id}

		)


     	and time between #{startTime} and #{endTime}


     	and category in(

			#{id}

		)


     	and status in(

			#{id}

		)

		select count(id) as value,level as name from xhgmnet_eastcom_alarminfo where  level<5 and level>0

     	and level in(

			#{id}

		)


     	and neType in(

			#{id}

		)


     	and time between #{startTime} and #{endTime}

		group by name order by name asc

		select neType,count(id) as num from xhgmnet_eastcom_alarminfo where  1=1

     	and severity in(

			#{id}

		)


     	and neType in(

			#{id}

		)



     	and time between #{startTime} and #{endTime}

		group by neType order by neType asc

mapper.update:
update xhgmnet_operations_bsfault set sureOk=1 where id in(

#{id}

