mapper.update:
update xhgmnet_operations_bsfault set checkTag=-1 where id in(

	#{id}

)

mapper.select:
SELECT a.* ,b.bscIsEnable FROM xhgmnet_bs_bsc as a left join xhgmnet_bs_bsc_config as b on a.Id=b.bscId and a.bsId=b.bsId WHERE a.bsId = #{bsId}
and a.Id=#{id}
ORDER BY a.Id ASC LIMIT 0, 30

select a.bsrIsEnable,b.* from xhgmnet_bs_bsr_config as a left join xhgmnet_bs_bsr as b on a.bsrId=b.Id and a.bsId=b.bsId WHERE a.bsId = #{bsId}
and a.bsrId=#{id}
order by a.bsrId asc

SELECT * FROM xhgmnet_bs_dpx WHERE bsId = #{bsId}
and Id=#{id}
ORDER BY Id ASC LIMIT 0, 30

SELECT * FROM xhgmnet_bs_psm WHERE bsId = #{bsId}
and Id=#{id}
ORDER BY Id ASC LIMIT 0, 30

select a.bsId,
a.id, a.sysType,a.neId,a.neType,a.mscId,a.location, a.modType,a.modId,a.subModType,a.iselec, a.subModId,a.code,a.category,a.causeCode,a.sureOk,checkTag,voiceTag, a.orderId, CASE weekly WHEN 0 THEN '星期日' WHEN 1 THEN '星期一' WHEN 2 THEN '星期二' WHEN 3 THEN '星期三' WHEN 4 THEN '星期四' WHEN 5 THEN '星期五' WHEN 6 THEN '星期六' ELSE '未知' END weekly, a.time, a.from, a.severity, a.type, a.reason, a.nowDeal, a.imbusiness, a.dealResult , a.faultRecoveryTime, CASE WHEN a.faultRecoveryTime!="" THEN TIMESTAMPDIFF(MINUTE,a.time,a.faultRecoveryTime) WHEN a.faultRecoveryTime="" THEN "" END faultTimeTotal, a.content, a.faultHandlePerson, a.faultRecordPerson, a.zbdldm, a.faultType, a.status, a.level as alarmLevel, a.alarmTag,b.name,b.level,b.status as tag,b.hometype,b.period,b.address, c.reason as reason2,c.suggestion as suggestion2 from xhgmnet_operations_bsfault as a left join xhgmnet_bs_station as b on
a.bsId=b.bsId
left join xhgmnet_eastcom_knowledge as c on a.neType=c.neType and a.modType=c.modType and a.subModType=c.subModType and a.category=c.category and a.code=c.code and a.causeCode=c.causeCode and a.sysType=1 where a.level<5
and a.bsId=#{bsId}
and b.period=#{period}
and a.location like concat(concat('%',#{bsName}),'%')
and a.time between #{starttime} and #{endtime}
and a.level in(
#{id}
)
and a.alarmTag in(
#{id}
)
and a.sysType=#{sysType}
and a.sysType=2 and a.modType=3000 and(a.subModType=170300000000001 or (a.subModType=11 and a.subModId=1003))
and a.sysType=2 and a.modType=3000 and(a.subModType=80200000000001 or (a.subModType=51 and a.subModId=1003))
and a.neType=2 and a.modType=1000 and a.code=1 and a.category=2
and a.neType=4 and (a.subModId <1000 or a.subModId>1200) and a.modType=3 and a.subModId not in(select bsIds from xhgmnet_stationalarm)
and a.neType=101
and a.neType=102
and a.reason like '温度高%'
and a.code=112
order by a.time desc
limit #{start},#{limit}

select count(*) from xhgmnet_operations_bsfault where level<5
and bsId=#{bsId}
and bsId in(select bsId from xhgmnet_bs_station where period=#{period})
and location like concat(concat('%',#{bsName}),'%')
and time between #{starttime} and #{endtime}
and alarmTag=#{alarmTag}
and level in(
#{id}
)
and alarmTag in(
#{id}
)
and sysType=#{sysType}
and sysType=2 and modType=3000 and(subModType=170300000000001 or (subModType=11 and subModId=1003))
and sysType=2 and modType=3000 and(subModType=80200000000001 or (subModType=51 and subModId=1003))
and neType=2 and modType=1000 and code=1 and category=2
and neType=4 and (subModId <1000 or subModId>1200) and modType=3 and subModId not in(select bsIds from xhgmnet_stationalarm)
and neType=101
and neType=102
and reason like '温度高%'
and code=112

select a.*,TIMESTAMPDIFF(SECOND,a.time_break,a.time_restore) as sumtime ,b.name,b.period from xhgmnet_bsstatus_history as a left join xhgmnet_bs_station as b on a.bsId=b.bsId where a.time_restore is not null and (TIMESTAMPDIFF(MINUTE,a.time_break,a.time_restore)) <= #{bsflash} and a.bsId not in (select bsId from xhgmnet_bs_station where period=3)
and a.bsId=#{bsId}
and a.time_break between #{starttime} and #{endtime}
order by a.time_break desc limit #{start},#{limit}

select count(*) from xhgmnet_bsstatus_history
where time_restore is not
null and
(TIMESTAMPDIFF(MINUTE,time_break,time_restore)) <=
#{bsflash}
and
bsId not in (select bsId from xhgmnet_bs_station where
period=3)

	and bsId=#{bsId}


	and time_break between #{starttime} and #{endtime}

select a.*,TIMESTAMPDIFF(SECOND,a.time_break,a.time_restore) as sumtime ,b.name,b.period from xhgmnet_bsstatus_history as a left join xhgmnet_bs_station as b on a.bsId=b.bsId where a.time_restore is not null and (TIMESTAMPDIFF(MINUTE,a.time_break,a.time_restore)) <= #{bsflash} and a.bsId not in (select bsId from xhgmnet_bs_station where period=3)
and a.time_break between #{starttime} and #{endtime}
order by a.time_break desc limit 0,1000

select * from xhgmnet_sf_bs_alarm WHERE bsId=#{bsId}

and  RIGHT(zbdldm,2)="GZ"


