mapper.select:
select id, Call_Time, Call_id, Call_sys_id, Use_Time, Caller, Called,
Call_Type,
Call_TS_Id, Call_BS_BS, Call_RSSI, Call_Result, Call_Path
from ${dbname1} where 1=1

and Call_TS_Id=#{bsId}


	and Caller like concat(#{Caller},'%')


	and Called like concat(#{Called},'%')


	and Call_Time >=#{starttime,jdbcType=DATE}


	and Call_Time <=#{endtime,jdbcType=DATE}


	union all

	select id, Call_Time, Call_id, Call_sys_id, Use_Time, Caller,
	Called,
	Call_Type,
	Call_TS_Id, Call_BS_BS, Call_RSSI, Call_Result,
	Call_Path from ${dbname2} where
	1=1

and Call_TS_Id=#{bsId}


		and Caller like concat(#{Caller},'%')


		and Called like concat(#{Called},'%')


		and Call_Time >=#{starttime,jdbcType=DATE}


		and Call_Time <=#{endtime,jdbcType=DATE}





order by Call_Time desc limit #{start},#{limit}

select sum(num) from(
select count(*) as num from ${dbname1} where 1=1

and Call_TS_Id=#{bsId}


	and Caller like concat(#{Caller},'%')


	and Called like concat(#{Called},'%')


	and Call_Time >=#{starttime,jdbcType=DATE}


	and Call_Time <=#{endtime,jdbcType=DATE}



) as tb





	union all
	select count(*) as num from ${dbname1} where 1=1

and Call_TS_Id=#{bsId}


		and Caller like concat(#{Caller},'%')


		and Called like concat(#{Called},'%')


		and Call_Time >=#{starttime,jdbcType=DATE}


		and Call_Time <=#{endtime,jdbcType=DATE}

	) as tb

select sum(Use_Time) as usetime,count(id) as num, DATE_FORMAT(Call_Time, 'd-%d' ) AS date from ${dbname} where 1=1
select sum(Use_Time) as usetime,count(id) as num ,DATE_FORMAT(Call_Time, 'h-%H' ) AS date from ${dbname} where date_format(Call_Time,'%Y-%m-%d')=#{time}
and Call_TS_Id=#{bsId}
group by date order by date asc
