mapper.select:
select * from xhgmnet_gpsoperation where 1=1


	and srcId in(

		#{id}

	)



	and srcId like concat('%',#{srcId},'%')


	and dstId like concat('%',#{dstId},'%')


	and infoTime between #{startTime} and #{endTime}

order by writeTime desc limit #{start},#{limit}

select SUM(cnt) from (
select count(*) cnt from xhgmnet_gpsoperation
where 1=1


	and srcId in(

		#{id}

	)



	and srcId like concat('%',#{srcId},'%')


	and dstId like concat('%',#{dstId},'%')


	and infoTime between #{startTime} and #{endTime}

) as tempTable

		select id,writeTime,operationOpt,dstId,status  FROM xhgmnet_gpsoperation
		WHERE id IN(select MAX(id) as id FROM xhgmnet_gpsoperation GROUP BY
		dstId) and dstId in(

            #{x}

        ) and operationOpt=#{type}
        GROUP BY dstId
